class MainWindow(QtWidgets.QMainWindow, Ui_Widget):
    def __init__(self, *args, obj=None, **kwargs):
        super(MainWindow, self).__init__(*args, **kwargs)
        self.setupUi(self)
        self.load_logo()
        self.make_key_connections()
        self.update_working_colors()
        self.active_directory = working_dir+"/"
        self.profile_directory = active_profile_dir+"/active_profile"
        self.script_directory = active_profile_dir+"/"
        
    def change_ad(self,directory):
        self.active_directory = directory
        
    def change_pd(self,directory):
        self.profile_directory = directory
    
    def save_clicked(self):
        self.save_file()
    
    def load_clicked(self):
        self.open_file()
        
    def runscript_clicked(self):
        try:
            subprocess.run(["python3",self.script_directory+"Set_Bloody_Key_Color.py"])
        except:
            self.runscript.setStyleSheet('QPushButton {background-color: red; color: black;}')
        
    def parse_clicked(self):
        fname = QFileDialog.getSaveFileName(self,"Parse file",self.profile_directory,'')
        if fname[0] != '':
            file = open(fname[0],'w')
            file.write('')
            file.close()
            file = open(fname[0], 'a')
            line = ""
            for index in range(0,58):
                line = line + str(colors_working[index])[1]+ str(colors_working[index])[2]
            file.write(line+"\n") 
            line = ""
            for index in range(58,104):
                line = line + str(colors_working[index])[1]+ str(colors_working[index])[2]
            file.write(line+"\n")
            line = ""
            for index in range(0,58):
                line = line + str(colors_working[index])[3]+ str(colors_working[index])[4]
            file.write(line+"\n") 
            line = ""
            for index in range(58,104):
                line = line + str(colors_working[index])[3]+ str(colors_working[index])[4]
            file.write(line+"\n")
            line = ""
            for index in range(0,58):
                line = line + str(colors_working[index])[5]+ str(colors_working[index])[6]
            file.write(line+"\n") 
            line = ""
            for index in range(58,104):
                line = line + str(colors_working[index])[5]+ str(colors_working[index])[6]
            file.write(line)
            file.close()
            self.change_pd(fname[0])
    
    def open_file(self):
        fname = QFileDialog.getOpenFileName(self, 'Open file', self.active_directory)
        if fname[0] != '':
            self.change_ad(fname[0])
            file = open(fname[0],'r')
            lines = []
            for line in file:
                line = line.strip()
                lines.append(line)
            file.close()
            for index in range(0,len(lines)):
                colors_working[index] = lines[index]
                self.update_working_colors()

    def save_file(self):
        fname = QFileDialog.getSaveFileName(self, 'Save file', self.active_directory)
        if fname[0] != '':
            file = open(fname[0],'w')
            file.write('')
            file.close()
            file = open(fname[0], 'a')
            for index in range(0,len(colors_working)):
                file.write(colors_working[index])
                if index != (len(colors_working)-1):
                    file.write("\n")
            file.close()
        
    def setall_clicked(self):
        color_dialog = QColorDialog.getColor()
        color_value = color_dialog.name()
        self.key_set_all(color_value)
     
    def key_set_color(self,index):
        color_dialog = QColorDialog.getColor()
        color_value = color_dialog.name()
        if color_value != colors_working[index]:
            colors_working[index] = color_value
            self.update_working_colors()
            return True
        else:
            return False
 
    def key_set_all(self,color):
        for index in range(0,len(colors_working)):
            colors_working[index] = str(color)
        self.update_working_colors()    
        
