        if os.path.exists('bloody_logo.png'):
            label = QLabel(self)
            pixmap = QPixmap('bloody_logo.png')
            label.setPixmap(pixmap)
            label.setScaledContents(True)
            label.setGeometry(470,13,325,130)
    
